// HasLcmClient applies the HasEdge predicate on the "lcm_client" edge.
func HasLcmClient() predicate.MtlsCertificateRequest {
	return predicate.MtlsCertificateRequest(func(s *sql.Selector) {
		step := sqlgraph.NewStep(
			sqlgraph.From(Table, FieldID),
			sqlgraph.Edge(sqlgraph.M2O, true, LcmClientTable, LcmClientColumn),
		)
		sqlgraph.HasNeighbors(s, step)
	})
}

// HasLcmClientWith applies the HasEdge predicate on the "lcm_client" edge with a given conditions (other predicates).
func HasLcmClientWith(preds ...predicate.LcmClient) predicate.MtlsCertificateRequest {
	return predicate.MtlsCertificateRequest(func(s *sql.Selector) {
		step := newLcmClientStep()
		sqlgraph.HasNeighborsWith(s, step, func(s *sql.Selector) {
			for _, p := range preds {
				p(s)
			}
		})
	})
}

